let elementChecked=document.querySelectorAll(".heart");for(let e of elementChecked)e.addEventListener("click",()=>{e.classList.toggle("heart--active")});let btnChecked=document.querySelectorAll(".btn");for(let e of btnChecked)e.addEventListener("click",()=>{e.style.display="none",e.nextElementSibling.style.display="flex"});let minusChecked=document.querySelectorAll(".cart__minus");for(let e of minusChecked)e.addEventListener("click",()=>{let l=parseInt(e.nextElementSibling.innerHTML.replace("шт",""));1==l?(e.closest(".cart").style.display="none",e.parentNode.previousElementSibling.style.display="block"):(l-=1,e.nextElementSibling.innerHTML=l+" шт")});let plusChecked=document.querySelectorAll(".cart__plus");for(let e of plusChecked)e.addEventListener("click",()=>{let l=parseInt(e.previousElementSibling.innerHTML.replace("шт",""));l+=1,e.previousElementSibling.innerHTML=l+" шт"});