let elementChecked=document.querySelectorAll(".heart");for(let e of elementChecked)e.addEventListener("click",()=>{e.classList.toggle("heart--active")});let btnChecked=document.querySelectorAll(".btn");for(let e of btnChecked)e.addEventListener("mouseover",()=>{e.classList.add("btn--active")}),e.addEventListener("mouseleave",()=>{e.classList.remove("btn--active")}),e.addEventListener("click",()=>{setTimeout(()=>{e.style.display="none",e.nextElementSibling.style.display="flex"},250)});let minusChecked=document.querySelectorAll(".cart__minus");for(let e of minusChecked)e.addEventListener("click",()=>{let t=parseInt(e.nextElementSibling.innerHTML.replace("шт",""));if(1==t){e.closest(".cart").style.display="none";let t=e.parentNode.previousElementSibling;t.style.display="block",t.addEventListener("mouseover",()=>{t.classList.add("btn--active")}),t.addEventListener("mouseleave",()=>{t.classList.remove("btn--active")})}else t-=1,e.nextElementSibling.innerHTML=t+" шт"});let plusChecked=document.querySelectorAll(".cart__plus");for(let e of plusChecked)e.addEventListener("click",()=>{let t=parseInt(e.previousElementSibling.innerHTML.replace("шт",""));t+=1,e.previousElementSibling.innerHTML=t+" шт"});